function selectEmojis() {
  const emojiCount = query.matches ? 4 : 6;
  return shuffle(emojis).slice(0, emojiCount);
}

export default function EmojiList() {
  const [emojiList, setEmojiList] = useState(selectEmojis());

  const queryRef = useRef(window.matchMedia('(max-width: 1000px)'));

  useEffect(() => {
    function handler() {
      setEmojiList(selectEmojis());
    }

    queryRef.current.addEventListener('change', handler);

    return () => { queryRef.current.removeEventListener('change', handler); };
  }, [queryRef.current, selectEmojis]);

  useEffect(() => {
    const interval = setInterval(() => {  
      setEmojiList(selectEmojis());
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className={styles.emojiList}>
      {emojiList.map((emoji, index) => <Emoji index={index} total={emojiList.length} emoji={emoji} key={index} />)}
    </div>
  );
}
